mapper.select:
select no, dong, aptName, code, buildYear, jibun
from houseinfo
where no = #{no}

select 	no, dong, AptName, code, buildYear, jibun
from 	houseinfo
where	dong 	= #{dong}
 	and code 	= #{code}
 	and aptName	= #{AptName}

mapper.update:
update houseinfo
	set	  dong = #{dong}
		, aptName = #{AptName}
		, code = #{code}
		, buildYear = #{buildYear}
		, jibun = #{jibun}
where no = #{no}
